# Python3 program to find all substring  
# that contain all vowels 
  
def isvowel(ch):
    # Check if character is vowel
    # return True if incoming character is vowel
    result = (ch == 'a' or ch == 'e' or ch == 'i' or ch == 'o' or ch == 'u')
    return result
def vowelsubstring(s):

    vowel_list = set()
    count = 0
    for i in range(len(s)):
        if isvowel(s[i]):
            vowel_list.add(s[i])
        
            if (len(vowel_list) == 5):
                count += 1
        else:
            vowel_list.clear()

    return count


str = ""; 
print(vowelsubstring(str))
  